INSERT INTO BOOK(
	BOOK_ID, TITLE, CATEGORY, PRICE
)VALUES(
	#{book_id}, #{title}, #{category}, #{price}
)

mapper.update:
UPDATE BOOK
SET
	TITLE = #{title},
	CATEGORY = #{category},
	PRICE = #{price}
WHERE BOOK_ID = #{bookId}

mapper.select:
SELECT B.* FROM ( SELECT A.*, ROW_NUMBER() OVER (ORDER BY A.BOOK_ID DESC) RNUM FROM ( SELECT BOOK_ID, TITLE, CATEGORY, PRICE, TO_DATE(INSERT_DATE, 'yy-mm-dd hh24:mi:ss') INSERT_DATE FROM BOOK WHERE 1=1
ORDER BY BOOK_ID DESC ) A ) B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}
